import base64

coded = "4.5.3!!B1UAAAEPDwAAAAAAAIA/AAAAPwAAwD8AANxBAgMFAQAAAAIADwAAAHgAAAAAGQAAAQEEBQAAAAMAAAAKHgIAAAACAAAPBQQAAAADAAA8CgADAAAAAgAAHg8="

coded_string = coded.split("!")
decoded_string = base64.b64decode(coded_string[1])
decoded_byte = decoded_string.decode('utf-8', errors='replace')

print("Version: ", coded_string[0], "\n\nSettings:\n", decoded_byte, "\n\nVanilla Settings:\n", coded_string[2])
